mapper.select:
select product_id, product_name, product_price, product_stock, product_description,
product_icon, product_status, sell_id, category_type, create_time, update_time
from product_info where sell_id=#{sellId} and category_type=#{TypeId} and product_status =1 limit #{currentPage},#{pageSize}

select product_id, product_name, product_price, product_stock, product_description,
product_icon, product_status, sell_id, category_type, create_time, update_time
from product_info where sell_id=#{sellId} and product_status =1  limit #{currentPage},#{pageSize}

select product_id, product_name, product_price, product_stock, product_description,
product_icon, product_status, sell_id, category_type, create_time, update_time
from product_info where sell_id=#{sellId}

  and product_name like "%"#{name}"%"


  and category_type=#{TypeId}


  and product_status=#{Status}


 limit #{currentPage},#{pageSize}

select count(1)
from product_info where sell_id=#{sellId}

  and product_name like "%"#{name}"%"


  and category_type=#{TypeId}



  and product_status=#{Status}
